       public void RegisterEventHandler()
       {
           _events.SelectionChange += Events_SelectionChangeEvent;
           _events.Register();
       }
       
       public void UnRegisterEventHandler()
       {
           _events.UnRegister();
       }
       
       void Events_SelectionChangeEvent()
